import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { getEvents, deleteEvent } from '../services/api';

const EventsPage = () => {
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      setLoading(true);
      const data = await getEvents();
      setEvents(data || []);
      setError(null);
    } catch (err) {
      console.error("Error fetching events:", err);
      setError("Failed to load events. Please try again later.");
      setEvents([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateEvent = () => {
    navigate('/events/new');
  };

  const handleEditEvent = (id) => {
    navigate(`/events/edit/${id}`);
  };

  const handleRegisterForEvent = (id) => {
    // For now, just show an alert since we haven't implemented registration yet
    alert('Registration functionality is coming soon!');
  };

  const handleDeleteEvent = async (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      try {
        await deleteEvent(id);

        // Update the events list using the functional update pattern
        setEvents((currentEvents) => currentEvents.filter(event => event.id !== id));

        // Alert the user that the event was deleted
        alert('Event was successfully deleted');
      } catch (err) {
        console.error("Error deleting event:", err);
        setError("Failed to delete event. Please try again.");
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Date not available';

    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    try {
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (err) {
      console.error('Error formatting date:', err);
      return 'Invalid date';
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto p-4 text-center">
        <p className="text-gray-600">Loading events...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto p-4">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          <p>{error}</p>
        </div>
        <button 
          onClick={fetchEvents}
          className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
        >
          Retry
        </button>
      </div>
    );
  }

  // Ensure events is an array
  const eventsList = Array.isArray(events) ? events : [];

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">All Events</h1>
        <button 
          onClick={handleCreateEvent}
          className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded shadow"
        >
          Create New Event
        </button>
      </div>

      {eventsList.length === 0 ? (
        <div className="bg-white rounded-lg shadow p-6 text-center">
          <p className="text-gray-500">No events found. Create your first event!</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {eventsList.map(event => (
            <div key={event?.id || Math.random()} className="bg-white rounded-lg shadow overflow-hidden">
              <div className="p-6">
                <Link to={`/events/${event?.id}`}>
                  <h2 className="text-xl font-bold mb-2 hover:text-blue-600">{event?.title || 'Untitled Event'}</h2>
                </Link>
                <p className="text-gray-600 mb-1">Type: {event?.event_type || 'No type specified'}</p>
                <p className="text-gray-600 mb-1">Date: {formatDate(event?.event_date)}</p>
                <p className="text-gray-600 mb-4">
                  <span className="font-semibold">Seats:</span> {event?.available_seats || (event?.seats - (event?.registrations || 0)) || 0} available 
                  {event?.registrations > 0 && (
                    <span className="text-blue-600 ml-2">
                      ({event.registrations} {event.registrations === 1 ? 'registration' : 'registrations'})
                    </span>
                  )}
                </p>

                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEditEvent(event?.id)}
                    className="bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded text-sm"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleRegisterForEvent(event?.id)}
                    className="bg-green-500 hover:bg-green-600 text-white py-1 px-3 rounded text-sm"
                  >
                    Register
                  </button>
                  <button
                    onClick={() => handleDeleteEvent(event?.id)}
                    className="bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded text-sm"
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EventsPage;
