import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getEvent, createEvent, updateEvent } from '../services/api';

const EventFormPage = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEditMode = id && id !== 'new';

  const [loading, setLoading] = useState(isEditMode);
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const [form, setForm] = useState({
    title: '',
    event_type: '',
    event_date: new Date().toISOString().split('.')[0],
    seats: 0
  });

  // Add registration count info when in edit mode
  const [registrationsCount, setRegistrationsCount] = useState(0);

  useEffect(() => {
    const fetchEventData = async () => {
      if (isEditMode) {
        try {
          setLoading(true);
          const eventData = await getEvent(parseInt(id));
          // Format the date to be compatible with the datetime-local input
          const formattedDate = new Date(eventData.event_date)
            .toISOString()
            .slice(0, 16);

          setForm({
            title: eventData.title,
            event_type: eventData.event_type,
            event_date: formattedDate,
            seats: eventData.seats
          });

          // Store registrations count for display
          if (eventData.registrations !== undefined) {
            setRegistrationsCount(eventData.registrations);
          }

          setError(null);
        } catch (err) {
          console.error("Error fetching event:", err);
          setError("Failed to load event data. Please try again.");
        } finally {
          setLoading(false);
        }
      }
    };

    fetchEventData();
  }, [id, isEditMode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'seats' ? parseInt(value) : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      // Create a properly formatted object for the API
      const eventData = {
        ...form,
        event_date: new Date(form.event_date).toISOString()
      };

      if (isEditMode) {
        await updateEvent(parseInt(id), eventData);
        // Navigate to confirmation page for edit
        navigate('/confirmation', {
          state: {
            type: 'event-updated',
            data: { ...form, id: parseInt(id) }
          }
        });
      } else {
        const newId = await createEvent(eventData);
        // Navigate to confirmation page for creation
        navigate('/confirmation', {
          state: {
            type: 'event-created',
            data: { ...form, id: newId }
          }
        });
      }
    } catch (err) {
      console.error("Error saving event:", err);
      setError("Failed to save event. Please check your inputs and try again.");
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto p-4 text-center">
        <p className="text-gray-600">Loading event data...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">{isEditMode ? 'Edit Event' : 'Create New Event'}</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          <p>{error}</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="bg-white rounded-lg shadow-md p-6">
        <div className="mb-4">
          <label htmlFor="title" className="block text-gray-700 font-bold mb-2">
            Event Title
          </label>
          <input
            type="text"
            id="title"
            name="title"
            value={form.title}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
            disabled={submitting}
          />
        </div>

        <div className="mb-4">
          <label htmlFor="event_type" className="block text-gray-700 font-bold mb-2">
            Event Type
          </label>
          <select
            id="event_type"
            name="event_type"
            value={form.event_type}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
            disabled={submitting}
          >
            <option value="">Select event type</option>
            <option value="Conference">Conference</option>
            <option value="Workshop">Workshop</option>
            <option value="Meetup">Meetup</option>
            <option value="Concert">Concert</option>
            <option value="Other">Other</option>
          </select>
        </div>

        <div className="mb-4">
          <label htmlFor="event_date" className="block text-gray-700 font-bold mb-2">
            Event Date and Time
          </label>
          <input
            type="datetime-local"
            id="event_date"
            name="event_date"
            value={form.event_date}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
            disabled={submitting}
          />
        </div>

        <div className="mb-6">
          <label htmlFor="seats" className="block text-gray-700 font-bold mb-2">
            Number of Seats
            {isEditMode && registrationsCount > 0 && (
              <span className="text-blue-600 ml-2 font-normal">
                ({registrationsCount} {registrationsCount === 1 ? 'registration' : 'registrations'} already made)
              </span>
            )}
          </label>
          <input
            type="number"
            id="seats"
            name="seats"
            value={form.seats}
            onChange={handleChange}
            min={isEditMode ? registrationsCount : 1}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
            disabled={submitting}
          />
          {isEditMode && (
            <p className="text-sm text-gray-600 mt-1">
              Available seats: {form.seats - registrationsCount}
            </p>
          )}
        </div>

        <div className="flex justify-between">
          <button
            type="button"
            onClick={() => navigate('/events')}
            className="bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            disabled={submitting}
          >
            Cancel
          </button>
          <button
            type="submit"
            className={`bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded focus:outline-none focus:shadow-outline ${
              submitting ? 'opacity-50 cursor-not-allowed' : ''
            }`}
            disabled={submitting}
          >
            {submitting 
              ? (isEditMode ? 'Updating...' : 'Creating...') 
              : (isEditMode ? 'Update Event' : 'Create Event')
            }
          </button>
        </div>
      </form>
    </div>
  );
};

export default EventFormPage;
