import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getEvent, createRegistration } from '../services/api';

const RegistrationFormPage = () => {
  const navigate = useNavigate();
  const { eventId } = useParams();
  const [eventDetails, setEventDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const [form, setForm] = useState({
    event_id: parseInt(eventId || '0'),
    first_name: '',
    last_name: ''
  });

  useEffect(() => {
    const fetchEventDetails = async () => {
      if (!eventId) {
        navigate('/events');
        return;
      }

      try {
        setLoading(true);
        const data = await getEvent(parseInt(eventId));
        setEventDetails(data);
        setError(null);
      } catch (err) {
        console.error("Error fetching event details:", err);
        setError("Failed to load event details. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchEventDetails();
  }, [eventId, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      // Submit registration to API
      const registrationId = await createRegistration(form);

      // Navigate to confirmation page
      navigate('/confirmation', {
        state: {
          type: 'registration',
          data: {
            ...form,
            id: registrationId,
            event_title: eventDetails.title,
            event_date: eventDetails.event_date,
            event_type: eventDetails.event_type
          }
        }
      });
    } catch (err) {
      console.error("Error registering for event:", err);
      setError(err.message || "Failed to register for event. Please try again.");
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto p-4 text-center">
        <p className="text-gray-600">Loading event details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto p-4">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          <p>{error}</p>
        </div>
        <button
          onClick={() => navigate('/events')}
          className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
        >
          Back to Events
        </button>
      </div>
    );
  }

  if (!eventDetails) {
    return (
      <div className="container mx-auto p-4 text-center">
        <p className="text-red-600">Event not found</p>
        <button
          onClick={() => navigate('/events')}
          className="mt-4 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
        >
          Back to Events
        </button>
      </div>
    );
  }

  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-2">Register for Event</h1>
      <h2 className="text-xl text-gray-600 mb-6">{eventDetails.title}</h2>

      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <h3 className="font-bold mb-3">Event Details</h3>
        <p className="mb-1"><span className="font-semibold">Type:</span> {eventDetails.event_type}</p>
        <p className="mb-1"><span className="font-semibold">Date:</span> {formatDate(eventDetails.event_date)}</p>
        <p className="mb-1">
          <span className="font-semibold">Seats:</span> 
          <span className={eventDetails.available_seats < 5 ? "text-red-600 font-semibold" : ""}>
            {eventDetails.available_seats} available
          </span> 
          {eventDetails.registrations > 0 && (
            <span className="text-blue-600 ml-2">
              (out of {eventDetails.seats} total)
            </span>
          )}
        </p>
      </div>

      <form onSubmit={handleSubmit} className="bg-white rounded-lg shadow-md p-6">
        <div className="mb-4">
          <label htmlFor="first_name" className="block text-gray-700 font-bold mb-2">
            First Name
          </label>
          <input
            type="text"
            id="first_name"
            name="first_name"
            value={form.first_name}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
            disabled={submitting}
          />
        </div>

        <div className="mb-6">
          <label htmlFor="last_name" className="block text-gray-700 font-bold mb-2">
            Last Name
          </label>
          <input
            type="text"
            id="last_name"
            name="last_name"
            value={form.last_name}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
            disabled={submitting}
          />
        </div>

        <div className="flex justify-between">
          <button
            type="button"
            onClick={() => navigate('/events')}
            className="bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            disabled={submitting}
          >
            Cancel
          </button>
          <button
            type="submit"
            className={`bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded focus:outline-none focus:shadow-outline ${
              submitting ? 'opacity-50 cursor-not-allowed' : ''
            }`}
            disabled={submitting}
          >
            {submitting ? 'Registering...' : 'Register'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default RegistrationFormPage;
