import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { getEvent, deleteEvent } from '../services/api';
import '../styles/mts-theme.css';
import { CalendarIcon, LocationIcon, UserIcon } from '../components/Icons';

const EventDetailsPage = () => {
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        setLoading(true);
        const data = await getEvent(id);
        setEvent(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load event details. Please try again later.');
        setLoading(false);
      }
    };

    fetchEvent();
  }, [id]);

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      try {
        await deleteEvent(id);
        navigate('/confirmation?action=event-deleted');
      } catch (err) {
        setError('Failed to delete event. Please try again later.');
      }
    }
  };

  // Check if user is the creator of the event
  const isEventCreator = () => {
    if (!event || !event.creator) return false;

    const user = JSON.parse(localStorage.getItem('user'));
    return user && user.id === event.creator.id;
  };

  // Check if the event date is in the past
  const isEventPast = () => {
    if (!event || !event.date) return false;

    const eventDate = new Date(event.date);
    const currentDate = new Date();
    return eventDate < currentDate;
  };

  // Check if the event is fully booked
  const isFullyBooked = () => {
    if (!event) return false;

    const registrations = event.registrations_count || (event.registrations ? event.registrations.length : 0);
    return event.capacity && registrations >= event.capacity;
  };

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return 'Date not specified';

    const options = { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-12 flex justify-center">
        <div className="mts-spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="mts-card p-6">
          <div className="mts-error">{error}</div>
          <div className="mt-4">
            <Link to="/events" className="mts-btn-secondary">
              Back to Events
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (!event) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="mts-card p-6">
          <div className="mts-error">Event not found</div>
          <div className="mt-4">
            <Link to="/events" className="mts-btn-secondary">
              Back to Events
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-12">
      <div className="mts-card">
        {/* Event Header */}
        <div className="p-6 border-b border-gray-200">
          <div className="flex justify-between items-start">
            <h1 className="text-3xl font-bold mts-text-primary">{event.title}</h1>

            {isEventCreator() && (
              <div className="flex space-x-2">
                <Link 
                  to={`/events/${id}/edit`}
                  className="mts-btn-secondary"
                >
                  Edit
                </Link>
                <button 
                  onClick={handleDelete}
                  className="mts-btn-danger"
                >
                  Delete
                </button>
              </div>
            )}
          </div>

          {event.creator && (
            <p className="mt-2 text-gray-600">
              Organized by: {event.creator.username}
            </p>
          )}
        </div>

        {/* Event Details */}
        <div className="p-6">
          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-2 mts-text-primary">Event Details</h2>
            <p className="text-gray-700 whitespace-pre-line">
              {event.description || 'No description provided'}
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
            <div className="flex items-center">
              <CalendarIcon className="h-5 w-5 mts-text-primary mr-2" />
              <span>{formatDate(event.date)}</span>
            </div>

            <div className="flex items-center">
              <LocationIcon className="h-5 w-5 mts-text-primary mr-2" />
              <span>{event.location || 'Location not specified'}</span>
            </div>

            <div className="flex items-center">
              <UserIcon className="h-5 w-5 mts-text-primary mr-2" />
              <span>
                {event.capacity 
                  ? `${event.registrations_count || (event.registrations ? event.registrations.length : 0)}/${event.capacity} participants`
                  : 'Unlimited capacity'}
              </span>
            </div>
          </div>

          {/* Call to Action */}
          <div className="mt-8 flex flex-col sm:flex-row sm:justify-between sm:items-center space-y-4 sm:space-y-0 sm:space-x-4">
            <Link to="/events" className="mts-btn-secondary">
              Back to Events
            </Link>

            {!isEventCreator() && !isEventPast() && !isFullyBooked() && (
              <Link to={`/events/${id}/register`} className="mts-btn-primary">
                Register for this Event
              </Link>
            )}

            {isFullyBooked() && !isEventCreator() && (
              <span className="mts-badge-warning py-2 px-4">
                This event is fully booked
              </span>
            )}

            {isEventPast() && (
              <span className="mts-badge-danger py-2 px-4">
                This event has already passed
              </span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetailsPage;
