import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const EventsPage = () => {
  const navigate = useNavigate();
  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Tech Conference 2025',
      event_type: 'Conference',
      event_date: '2025-07-15T09:00:00',
      seats: 200,
    },
    {
      id: 2,
      title: 'Web Development Workshop',
      event_type: 'Workshop',
      event_date: '2025-08-10T14:00:00',
      seats: 30,
    },
    {
      id: 3,
      title: 'Networking Meetup',
      event_type: 'Meetup',
      event_date: '2025-06-05T18:00:00',
      seats: 50,
    }
  ]);

  const handleCreateEvent = () => {
    navigate('/events/new');
  };

  const handleEditEvent = (id) => {
    navigate(`/events/edit/${id}`);
  };

  const handleRegisterForEvent = (id) => {
    navigate(`/register/${id}`);
  };

  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">All Events</h1>
        <button 
          onClick={handleCreateEvent}
          className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded shadow"
        >
          Create New Event
        </button>
      </div>

      {events.length === 0 ? (
        <div className="bg-white rounded-lg shadow p-6 text-center">
          <p className="text-gray-500">No events found. Create your first event!</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {events.map(event => (
            <div key={event.id} className="bg-white rounded-lg shadow overflow-hidden">
              <div className="p-6">
                <h2 className="text-xl font-bold mb-2">{event.title}</h2>
                <p className="text-gray-600 mb-1">Type: {event.event_type}</p>
                <p className="text-gray-600 mb-1">Date: {formatDate(event.event_date)}</p>
                <p className="text-gray-600 mb-4">Available Seats: {event.seats}</p>

                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEditEvent(event.id)}
                    className="bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded text-sm"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleRegisterForEvent(event.id)}
                    className="bg-green-500 hover:bg-green-600 text-white py-1 px-3 rounded text-sm"
                  >
                    Register
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EventsPage;
