import React, { useState, useEffect } from 'react';
import { Link, useParams, useLocation } from 'react-router-dom';
import { getEvent, getRegistration } from '../services/api';
import '../styles/mts-theme.css';
import { CalendarIcon, LocationIcon, UserIcon } from '../components/Icons';

const ConfirmationPage = () => {
  const { eventId, registrationId } = useParams();
  const location = useLocation();
  const [event, setEvent] = useState(null);
  const [registration, setRegistration] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Try to get event and registration details from location state first
  // Fall back to API if not available
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // If we have data from the registration flow, use it
        if (location.state?.event && location.state?.registration) {
          setEvent(location.state.event);
          setRegistration(location.state.registration);
          setLoading(false);
          return;
        }

        // Otherwise fetch the data
        let eventData = null;
        let registrationData = null;

        if (eventId) {
          eventData = await getEvent(eventId);
          setEvent(eventData);
        }

        if (registrationId) {
          registrationData = await getRegistration(registrationId);
          setRegistration(registrationData);
        }

        if (!eventData && !registrationData) {
          setError('No event or registration information found.');
        }

        setLoading(false);
      } catch (err) {
        setError('Failed to load confirmation details. Please check your dashboard for registration status.');
        setLoading(false);
      }
    };

    fetchData();
  }, [location.state, eventId, registrationId]);

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return 'Date not specified';

    const options = { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-12 flex justify-center">
        <div className="mts-spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="mts-card p-6">
          <div className="mts-error">{error}</div>
          <div className="mt-4 flex space-x-4">
            <Link to="/dashboard" className="mts-btn-primary">
              Go to Dashboard
            </Link>
            <Link to="/events" className="mts-btn-secondary">
              Browse Events
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-12">
      <div className="max-w-2xl mx-auto">
        <div className="mts-card p-6">
          <div className="text-center mb-6">
            <h1 className="text-3xl font-bold mts-text-primary mb-2">Registration Confirmed!</h1>
            <p className="text-gray-600">
              You're all set for {event?.title || 'the event'}
            </p>
          </div>

          {event && (
            <div className="mb-6">
              <h2 className="text-xl font-semibold mb-4">Event Details</h2>
              <div className="space-y-3">
                <div className="flex items-center">
                  <CalendarIcon className="h-5 w-5 mts-text-primary mr-3" />
                  <span>{formatDate(event.date)}</span>
                </div>

                {event.location && (
                  <div className="flex items-center">
                    <LocationIcon className="h-5 w-5 mts-text-primary mr-3" />
                    <span>{event.location}</span>
                  </div>
                )}

                <div className="flex items-center">
                  <UserIcon className="h-5 w-5 mts-text-primary mr-3" />
                  <span>
                    {event.capacity 
                      ? `${event.registrations_count || (event.registrations ? event.registrations.length : 0)}/${event.capacity} participants`
                      : 'Unlimited capacity'}
                  </span>
                </div>
              </div>
            </div>
          )}

          {registration && (
            <div className="mb-6">
              <h2 className="text-xl font-semibold mb-4">Registration Details</h2>
              <div className="space-y-2">
                <p>
                  <span className="font-medium">Registration ID:</span>{' '}
                  {registration.id}
                </p>
                {registration.created_at && (
                  <p>
                    <span className="font-medium">Registered on:</span>{' '}
                    {formatDate(registration.created_at)}
                  </p>
                )}
                {registration.notes && (
                  <div>
                    <p className="font-medium">Your notes:</p>
                    <p className="mt-1 bg-gray-50 p-3 rounded-lg">
                      {registration.notes}
                    </p>
                  </div>
                )}
              </div>
            </div>
          )}

          <div className="mt-8 flex flex-col sm:flex-row justify-center space-y-3 sm:space-y-0 sm:space-x-4">
            <Link to="/dashboard" className="mts-btn-primary">
              View My Dashboard
            </Link>
            <Link to="/events" className="mts-btn-secondary">
              Browse More Events
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ConfirmationPage;
