import React, { useEffect } from 'react';
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { CheckCircleIcon } from '../components/Icons';

const ConfirmationPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { type, data } = location.state || { type: 'generic', data: {} };

  // Redirect to home if accessed directly without state
  useEffect(() => {
    if (!location.state) {
      navigate('/', { replace: true });
    }
  }, [location.state, navigate]);

  // Auto-redirect after some time
  useEffect(() => {
    const timer = setTimeout(() => {
      navigate('/events', { replace: true });
    }, 5000); // Redirect after 5 seconds

    return () => clearTimeout(timer);
  }, [navigate]);

  const renderMessage = () => {
    switch (type) {
      case 'event-created':
        return (
          <>
            <h1 className="text-2xl font-bold mb-2">Event Created Successfully!</h1>
            <p className="mb-4">
              Your event "{data.title}" has been created and is now available for registrations.
            </p>
            <div className="mt-4 p-4 bg-gray-50 rounded-lg">
              <h2 className="font-bold mb-2">Event Details</h2>
              <p><span className="font-semibold">Title:</span> {data.title}</p>
              <p><span className="font-semibold">Type:</span> {data.event_type}</p>
              <p><span className="font-semibold">Date:</span> {formatDate(data.event_date)}</p>
              <p><span className="font-semibold">Seats:</span> {data.seats}</p>
            </div>
          </>
        );

      case 'event-updated':
        return (
          <>
            <h1 className="text-2xl font-bold mb-2">Event Updated Successfully!</h1>
            <p className="mb-4">
              Your event "{data.title}" has been updated.
            </p>
            <div className="mt-4 p-4 bg-gray-50 rounded-lg">
              <h2 className="font-bold mb-2">Updated Event Details</h2>
              <p><span className="font-semibold">Title:</span> {data.title}</p>
              <p><span className="font-semibold">Type:</span> {data.event_type}</p>
              <p><span className="font-semibold">Date:</span> {formatDate(data.event_date)}</p>
              <p><span className="font-semibold">Seats:</span> {data.seats}</p>
            </div>
          </>
        );

      case 'event-deleted':
        return (
          <>
            <h1 className="text-2xl font-bold mb-2">Event Deleted Successfully!</h1>
            <p className="mb-4">
              The event "{data.title}" has been permanently deleted.
            </p>
            {data && (
              <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                <h2 className="font-bold mb-2">Deleted Event Details</h2>
                <p><span className="font-semibold">Title:</span> {data.title}</p>
                <p><span className="font-semibold">Type:</span> {data.event_type}</p>
                <p><span className="font-semibold">Date:</span> {formatDate(data.event_date)}</p>
              </div>
            )}
          </>
        );

      case 'registration':
        return (
          <>
            <h1 className="text-2xl font-bold mb-2">Registration Successful!</h1>
            <p className="mb-4">
              {data.first_name} {data.last_name}, you have successfully registered for "{data.event_title}".
            </p>
            <div className="mt-4 p-4 bg-gray-50 rounded-lg">
              <h2 className="font-bold mb-2">Registration Details</h2>
              <p><span className="font-semibold">Name:</span> {data.first_name} {data.last_name}</p>
              <p><span className="font-semibold">Event:</span> {data.event_title}</p>
              <p><span className="font-semibold">Date:</span> {data.event_date ? formatDate(data.event_date) : 'Not specified'}</p>
            </div>
          </>
        );

      default:
        return (
          <>
            <h1 className="text-2xl font-bold mb-2">Success!</h1>
            <p>Your action has been completed successfully.</p>
          </>
        );
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Not specified';

    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (!location.state) {
    return null; // Will redirect in the useEffect
  }

  return (
    <div className="container mx-auto p-4">
      <div className="bg-white rounded-lg shadow-md p-8 max-w-2xl mx-auto my-8">
        <div className="flex flex-col items-center text-center mb-6">
          <CheckCircleIcon className="w-16 h-16 text-green-500 mb-4" />
          {renderMessage()}
          <p className="text-gray-500 mt-6">You will be redirected to the events page in 5 seconds.</p>
        </div>

        <div className="flex justify-center space-x-4 mt-8">
          <Link
            to="/events"
            className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded shadow"
          >
            View All Events
          </Link>
          <Link
            to="/"
            className="bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded shadow"
          >
            Return Home
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ConfirmationPage;
