import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { getEvent, deleteEvent } from '../services/api';
import { CalendarIcon, LocationIcon, UsersIcon } from '../components/Icons';

const EventDetailsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        setLoading(true);
        const eventData = await getEvent(id);
        setEvent(eventData);
        setError(null);
      } catch (err) {
        console.error('Error fetching event details:', err);
        setError('Failed to load event details. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchEventDetails();
  }, [id]);

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      try {
        await deleteEvent(id);
        navigate('/events');
        alert('Event successfully deleted');
      } catch (err) {
        console.error('Error deleting event:', err);
        setError('Failed to delete event. Please try again.');
      }
    }
  };

  const handleRegister = () => {
    alert('Registration functionality is coming soon!');
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Date not available';

    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    try {
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (err) {
      console.error('Error formatting date:', err);
      return 'Invalid date';
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <p className="text-gray-600">Loading event details...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          <p>{error}</p>
        </div>
        <button 
          onClick={() => navigate('/events')}
          className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
        >
          Back to Events
        </button>
      </div>
    );
  }

  if (!event) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <p className="text-gray-600">Event not found</p>
          <Link to="/events" className="mt-4 inline-block bg-blue-500 text-white px-4 py-2 rounded">
            Back to Events
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <Link to="/events" className="text-blue-600 hover:underline">
          &larr; Back to Events
        </Link>
      </div>

      <div className="bg-white rounded-lg shadow-lg overflow-hidden">
        <div className="p-6">
          <h1 className="text-3xl font-bold mb-4">{event.title}</h1>

          <div className="mb-6">
            <span className="inline-block bg-blue-100 text-blue-800 rounded-full px-3 py-1 text-sm font-semibold mr-2">
              {event.event_type}
            </span>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
            <div className="flex items-start">
              <CalendarIcon className="w-5 h-5 mr-2 text-gray-500 mt-1" />
              <div>
                <h3 className="text-lg font-medium">Date and Time</h3>
                <p className="text-gray-700">{formatDate(event.event_date)}</p>
              </div>
            </div>

            <div className="flex items-start">
              <LocationIcon className="w-5 h-5 mr-2 text-gray-500 mt-1" />
              <div>
                <h3 className="text-lg font-medium">Location</h3>
                <p className="text-gray-700">{event.location}</p>
              </div>
            </div>

            <div className="flex items-start">
              <UsersIcon className="w-5 h-5 mr-2 text-gray-500 mt-1" />
              <div>
                <h3 className="text-lg font-medium">Available Seats</h3>
                <p className="text-gray-700">
                  {event.available_seats || (event.seats - (event.registrations || 0))} available 
                  {event.registrations > 0 && ` (${event.registrations} registered)`}
                </p>
              </div>
            </div>
          </div>

          {event.description && (
            <div className="mb-8">
              <h3 className="text-xl font-semibold mb-2">Description</h3>
              <p className="text-gray-700 whitespace-pre-line">{event.description}</p>
            </div>
          )}

          <div className="flex justify-between items-center">
            <div className="space-x-2">
              <Link 
                to={`/events/edit/${event.id}`}
                className="inline-block bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
              >
                Edit Event
              </Link>
              <button 
                onClick={handleDelete}
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
              >
                Delete Event
              </button>
            </div>

            <button 
              onClick={handleRegister}
              className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded font-medium"
            >
              Register Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetailsPage;
