import React from 'react';
import { Link } from 'react-router-dom';
import { CalendarIcon, LocationIcon } from './Icons';

const EventCard = ({ event }) => {
  // Format the date
  const formatDate = (dateString) => {
    if (!dateString) return 'Date not specified';

    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Check if event is in the past
  const isPastEvent = () => {
    if (!event.date) return false;
    return new Date(event.date) < new Date();
  };

  // Check if event is fully booked
  const isFullyBooked = () => {
    const capacity = event.capacity || 0;
    const registrationsCount = event.registrations_count || 
                              (event.registrations ? event.registrations.length : 0);
    return capacity > 0 && registrationsCount >= capacity;
  };

  // Get remaining seats
  const getRemainingSeats = () => {
    const capacity = event.capacity || 0;
    const registrationsCount = event.registrations_count || 
                              (event.registrations ? event.registrations.length : 0);
    return Math.max(0, capacity - registrationsCount);
  };

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
      <Link to={`/events/${event.id}`} className="block">
        <div className="p-6">
          {/* Title */}
          <h2 className="text-xl font-bold text-gray-800 mb-2">{event.title || 'Untitled Event'}</h2>

          {/* Date and Location */}
          <div className="flex flex-col mb-4 text-gray-600">
            <div className="flex items-center mb-2">
              <CalendarIcon className="h-5 w-5 mr-2 text-gray-500" />
              <span>{formatDate(event.date)}</span>
            </div>

            {event.location && (
              <div className="flex items-center">
                <LocationIcon className="h-5 w-5 mr-2 text-gray-500" />
                <span>{event.location}</span>
              </div>
            )}
          </div>

          {/* Description */}
          <p className="text-gray-600 mb-4 line-clamp-2">
            {event.description || 'No description available'}
          </p>

          {/* Status indicators */}
          <div className="flex flex-wrap items-center justify-between mt-4">
            <div className="flex items-center space-x-2">
              {isPastEvent() && (
                <span className="bg-gray-200 text-gray-800 px-2 py-1 rounded text-xs">Past Event</span>
              )}
              {isFullyBooked() && !isPastEvent() && (
                <span className="bg-red-100 text-red-800 px-2 py-1 rounded text-xs">Fully Booked</span>
              )}
              {!isPastEvent() && !isFullyBooked() && event.capacity > 0 && (
                <span className="bg-green-100 text-green-800 px-2 py-1 rounded text-xs">
                  {getRemainingSeats()} seats left
                </span>
              )}
            </div>

            <span className="text-gray-500 text-sm">
              {event.registrations_count || (event.registrations ? event.registrations.length : 0)}/
              {event.capacity || '∞'} registered
            </span>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default EventCard;
