import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { getEvent, createRegistration } from '../services/api';
import { CalendarIcon, LocationIcon, UserIcon } from '../components/Icons';
import '../styles/mts-theme.css';

const RegisterForEventPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({ notes: '' });
  const [availableSeats, setAvailableSeats] = useState(0);

  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchEventData = async () => {
      try {
        const eventData = await getEvent(id);
        setEvent(eventData);

        // Check if user is logged in
        if (!currentUser) {
          setError('You must be logged in to register for events');
          return;
        }

        // Check if event is in the past
        const eventDate = new Date(eventData.event_date || eventData.date);
        if (eventDate < new Date()) {
          setError('You cannot register for past events');
          return;
        }

        // Check if user is the event creator
        if (currentUser.id === eventData.user_id) {
          setError('You cannot register for your own event');
          return;
        }

        // Calculate available seats
        const registeredCount = eventData.registrations_count || 0;
        const capacity = eventData.capacity || 0;
        const availableSeats = capacity - registeredCount;
        setAvailableSeats(availableSeats);

        // Check if event is fully booked
        if (capacity > 0 && availableSeats <= 0) {
          setError('This event is fully booked');
          return;
        }
      } catch (err) {
        setError(err.message || 'Failed to load event details');
        console.error('Error fetching event:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchEventData();
  }, [id, currentUser]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setSubmitting(true);
      await createRegistration({
        event_id: parseInt(id),
        notes: formData.notes
      });

      navigate(`/confirmation?action=registration-created&eventId=${id}`);
    } catch (err) {
      setError(err.message || 'Failed to register for event');
      console.error('Error registering for event:', err);
      setSubmitting(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Date not available';

    const date = new Date(dateString);
    if (isNaN(date.getTime())) return 'Date not available';

    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <Link to={`/events/${id}`} className="mts-btn-secondary">
          ← Back to Event
        </Link>
      </div>

      {loading ? (
        <div className="text-center py-10">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 mts-text-primary mx-auto"></div>
        </div>
      ) : error ? (
        <div className="mts-card p-6 mb-6">
          <h2 className="text-xl font-semibold mb-4 mts-text-error">Unable to Register</h2>
          <p className="mb-4">{error}</p>
          <Link to={`/events/${id}`} className="mts-btn-secondary">
            Back to Event
          </Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="mts-card p-6">
            <h1 className="text-2xl font-bold mb-4 mts-text-primary">Register for Event</h1>
            <div className="mb-6">
              <h2 className="text-xl font-semibold">{event?.title || 'Untitled Event'}</h2>
              <p className="text-gray-600 mb-4">{event?.event_type || 'No type specified'}</p>

              <div className="flex items-center text-gray-700 mb-3">
                <CalendarIcon className="w-5 h-5 mr-3" />
                <span>{formatDate(event?.event_date || event?.date)}</span>
              </div>

              {event?.location && (
                <div className="flex items-center text-gray-700 mb-3">
                  <LocationIcon className="w-5 h-5 mr-3" />
                  <span>{event.location}</span>
                </div>
              )}

              <div className="flex items-center text-gray-700 mb-4">
                <UserIcon className="w-5 h-5 mr-3" />
                <span>
                  {availableSeats} {availableSeats === 1 ? 'seat' : 'seats'} available
                </span>
              </div>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label htmlFor="notes" className="block mb-2 text-sm font-medium text-gray-700">
                  Additional Notes (Optional)
                </label>
                <textarea
                  id="notes"
                  name="notes"
                  rows="4"
                  className="mts-input-field w-full"
                  placeholder="Any special requirements or notes for the organizer"
                  value={formData.notes}
                  onChange={handleChange}
                ></textarea>
              </div>

              <button 
                type="submit"
                className="mts-btn-primary w-full py-3"
                disabled={submitting}
              >
                {submitting ? 'Processing...' : 'Confirm Registration'}
              </button>
            </form>
          </div>

          <div className="mts-card p-6 bg-gray-50">
            <h2 className="text-xl font-semibold mb-4">Registration Details</h2>
            <p className="mb-6">
              You are registering as <strong>{currentUser?.username}</strong>.
            </p>

            <div className="mb-6">
              <h3 className="font-medium mb-2">What happens after registration?</h3>
              <ul className="list-disc list-inside space-y-2 text-gray-700">
                <li>You'll receive a confirmation of your registration</li>
                <li>Your registration will be visible on your dashboard</li>
                <li>You can cancel your registration at any time from your dashboard</li>
                <li>The event organizer may contact you with further details</li>
              </ul>
            </div>

            <div className="p-4 bg-blue-50 border-l-4 border-blue-500 text-blue-700 mb-4">
              <p>By registering for this event, you agree to provide your contact information to the event organizer.</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RegisterForEventPage;
